import React, { useState, useEffect } from 'react';

function Survey({ participantNumber, age, education }) {
  const [questions, setQuestions] = useState([]);
  const [currentElementIndex, setCurrentElementIndex] = useState(0);
  const [currentStep, setCurrentStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [error, setError] = useState(null);

  // Fetch questions based on participantNumber
  useEffect(() => {
    fetch(`http://localhost:3001/questions/${participantNumber}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Invalid participant number');
        }
        return response.json();
      })
      .then(data => {
        setQuestions(data);
        // Initialize answers array based on number of questions
        setAnswers(data.map(() => [null, null, null, null]));
        setError(null);
      })
      .catch(error => setError(error.message));
  }, [participantNumber]);

  // Error or loading states
  if (error) {
    return <div>{error}</div>;
  }
  if (questions.length === 0) {
    return <div>Loading...</div>;
  }

  const currentElement = questions[currentElementIndex];
  const currentQuestion = getQuestion(currentStep, currentElement);

  const handleAnswer = (selectedOption) => {
    const newAnswers = [...answers];
    newAnswers[currentElementIndex][currentStep] = selectedOption;
    setAnswers(newAnswers);

    if (currentStep < 3) {
      setCurrentStep(currentStep + 1);
    } else if (currentElementIndex < questions.length - 1) {
      setCurrentElementIndex(currentElementIndex + 1);
      setCurrentStep(0);
    } else {
      // Survey complete, submit data
      fetch('http://localhost:3001/responses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          participantNumber,
          age,
          education,
          answers: newAnswers,
        }),
      })
        .then(response => response.text())
        .then(data => {
          console.log('Success:', data);
          alert('Survey submitted successfully!');
        })
        .catch(error => {
          console.error('Error:', error);
          alert('Failed to submit survey.');
        });
    }
  };

  const getQuestion = (step, element) => {
    const { type } = element;
  
    if (step === 0) {
      return {
        text: type === 1 ? "Do you think this claim is True or False?" : "Do you support or against this claim?",
        options: type === 1 ? ["True", "False"] : ["Support", "Against"]
      };
    } else if (step === 1) {
      return {
        text: "How certain are you about your choice? (1 = Not at all certain, 5 = Extremely certain)",
        options: [1, 2, 3, 4, 5]
      };
    } else if (step === 2) {
      return {
        text: type === 1 
          ? "After checking the AI response to the relevant question, do you think this claim is True or False?" 
          : "After checking the AI comment to the claim, do you support or against this claim?",
        options: type === 1 ? ["True", "False"] : ["Support", "Against"]
      };
    } else if (step === 3) {
      return {
        text: type === 1 
          ? "After checking the AI response to the relevant question, how certain are you about your choice? (1 = Not at all certain, 5 = Extremely certain)" 
          : "After checking the AI comment to the claim, how certain are you about your choice? (1 = Not at all certain, 5 = Extremely certain)",
        options: [1, 2, 3, 4, 5]
      };
    }
  };

  return (
    <div>
      <h2>Claim {currentElement.claim_number}: {currentElement.claim}</h2>
      {currentStep === 2 && (
        <div>
          {currentElement.type === 1 ? (
            <>
              <p><strong>Relevant Question:</strong> {currentElement.question}</p>
              <p><strong>AI Response:</strong> {currentElement.answer}</p>
            </>
          ) : (
            <p><strong>AI Comment:</strong> {currentElement.comment}</p>
          )}
        </div>
      )}
      <p>{currentQuestion.text}</p>
      <div>
        {currentQuestion.options.map(option => (
          <button key={option} onClick={() => handleAnswer(option)}>
            {option}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Survey;
