import React, { useState, useEffect } from 'react';

function Survey() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    fetch('http://localhost:3001/questions/1')
      .then(response => response.json())
      .then(data => setQuestions(data))
      .catch(error => console.error('Error fetching questions:', error));
  }, []);

  if (questions.length === 0) {
    return <div>Loading...</div>;
  }

  const currentQuestion = questions[currentQuestionIndex];
  const aiResponse = currentQuestion.type === 1 ? currentQuestion.answer : currentQuestion.comment;

  const handleNext = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      alert('Survey completed!');
    }
  };

  return (
    <div>
      <h2>Claim {currentQuestion.claim_number}</h2>
      <p><strong>Claim:</strong> {currentQuestion.claim}</p>
      {currentQuestion.type === 1 && (
        <>
          <p><strong>Question:</strong> {currentQuestion.question}</p>
          <p><strong>AI Answer:</strong> {currentQuestion.answer}</p>
        </>
      )}
      {currentQuestion.type === 2 && (
        <p><strong>AI Comment:</strong> {currentQuestion.comment}</p>
      )}
      <button onClick={handleNext}>
        {currentQuestionIndex < questions.length - 1 ? 'Next' : 'Finish'}
      </button>
    </div>
  );
}

export default Survey;
