import React, { useState, useEffect } from 'react';

function Survey() {
  // State declarations
  const [questions, setQuestions] = useState([]); // Array of survey elements from backend
  const [currentElementIndex, setCurrentElementIndex] = useState(0); // Index of the current element
  const [currentStep, setCurrentStep] = useState(0); // Step within the current element (0-3)
  const [answers, setAnswers] = useState([]); // Array to store user answers

  // Function to get the question text and options based on step and element
  const getQuestion = (step, element) => {
    const { type, claim } = element;
    if (step === 0) {
      return {
        text: type === 1 ? `Do you think this claim is True or False?` : `Do you support or against this claim?`,
        options: type === 1 ? ["True", "False"] : ["Support", "Against"]
      };
    } else if (step === 1) {
      return {
        text: "How certain are you about your choice? (1 = Not at all certain, 5 = Extremely certain)",
        options: [1, 2, 3, 4, 5]
      };
    } else if (step === 2) {
      return {
        text: type === 1 ? "After checking the AI response to the relevant question, do you think this claim is True or False?" : "After checking the AI comment to the claim, do you support or against this claim?",
        options: type === 1 ? ["True", "False"] : ["Support", "Against"]
      };
    } else if (step === 3) {
      return {
        text: type === 1 ? "After checking the AI response to the relevant question, how certain are you about your choice? (1 = Not at all certain, 5 = Extremely certain)" : "After checking the AI comment to the claim, how certain are you about your choice? (1 = Not at all certain, 5 = Extremely certain)",
        options: [1, 2, 3, 4, 5]
      };
    }
  };

  // Fetch questions and initialize answers on component mount
  useEffect(() => {
    fetch('http://localhost:3001/questions/1')
      .then(response => response.json())
      .then(data => {
        setQuestions(data);
        setAnswers(data.map(() => [null, null, null, null])); // Initialize answers array
      })
      .catch(error => console.error('Error fetching questions:', error));
  }, []);

  // Loading state
  if (questions.length === 0) {
    return <div>Loading...</div>;
  }

  // Current element and question
  const currentElement = questions[currentElementIndex];
  const currentQuestion = getQuestion(currentStep, currentElement);

  const handleAnswer = (selectedOption) => {
    // Update the answers state with the user's choice
    const newAnswers = [...answers];
    newAnswers[currentElementIndex][currentStep] = selectedOption;
    setAnswers(newAnswers);
  
    // Check if the survey is complete
    if (currentStep < 3) {
      setCurrentStep(currentStep + 1); // Move to the next question in the same element
    } else if (currentElementIndex < questions.length - 1) {
      setCurrentElementIndex(currentElementIndex + 1); // Move to the next element
      setCurrentStep(0); // Reset to the first question
    } else {
      // Survey is complete, send answers to the backend
      fetch('http://localhost:3001/responses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newAnswers),
      })
        .then(response => response.text())
        .then(data => {
          console.log('Success:', data); // Log the backend response
          alert('Survey submitted successfully!');
        })
        .catch(error => {
          console.error('Error submitting answers:', error);
          alert('Failed to submit survey.');
        });
    }
  };

  // Render the survey UI
  return (
    <div>
      <h2>Claim {currentElement.claim_number}: {currentElement.claim}</h2>
      {currentStep === 2 && (
        <div>
          {currentElement.type === 1 ? (
            <>
              <p><strong>Relevant Question:</strong> {currentElement.question}</p>
              <p><strong>AI Response:</strong> {currentElement.answer}</p>
            </>
          ) : (
            <p><strong>AI Comment:</strong> {currentElement.comment}</p>
          )}
        </div>
      )}
      <p>{currentQuestion.text}</p>
      <div>
        {currentQuestion.options.map(option => (
          <button key={option} onClick={() => handleAnswer(option)}>
            {option}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Survey;
