import React, { useState } from 'react';
import Login from './components/Login';
import IntroPage from './components/IntroPage';
import Survey from './components/Survey';
import './styles/App.css'; // Import the global stylesheet

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showIntro, setShowIntro] = useState(false);

  const [participantNumber, setParticipantNumber] = useState('');
  const [age, setAge] = useState('');
  const [education, setEducation] = useState('');

  const handleLogin = (data) => {
    setParticipantNumber(data.participantNumber);
    setAge(data.age);
    setEducation(data.education);
    setIsLoggedIn(true);
    setShowIntro(true); // Show intro page after login
  };

  const handleStartSurvey = () => {
    setShowIntro(false); // Hide intro and show survey
  };

  // return (
  //   <div className="app-wrapper"> {/* Optional wrapper for the entire app */}
  //     {isLoggedIn ? (
  //         <Survey
  //           participantNumber={participantNumber}
  //           age={age}
  //           education={education}
  //         />
  //     ) : (
  //       <Login onLogin={handleLogin} />
  //     )}
  //   </div>
  // );

  return (
    <div className="app-wrapper">
      {!isLoggedIn ? (
        <Login onLogin={handleLogin} />
      ) : showIntro ? (
        <IntroPage onStartSurvey={handleStartSurvey} />
      ) : (
        <Survey />
      )}
    </div>
  );
}

export default App;
