import React, { useState } from 'react';
import Login from './components/Login';
import IntroPage from './components/IntroPage';
import Survey from './components/Survey';
import EndPage from './components/EndPage';
import './styles/App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showIntro, setShowIntro] = useState(false);
  const [participantNumber, setParticipantNumber] = useState('');
  const [age, setAge] = useState('');
  const [education, setEducation] = useState('');
  const [isSurveyComplete, setIsSurveyComplete] = useState(false);
  const [userData, setUserData] = useState(null);

  const handleLogin = (data) => {
    console.log('Participant Number:', data.participantNumber); // Debug
    setParticipantNumber(data.participantNumber);
    setAge(data.age);
    setEducation(data.education);
    setIsLoggedIn(true);
    setShowIntro(true);
  };

  const handleStartSurvey = () => {
    setShowIntro(false);
  };

  const handleLoginComplete = (data) => {
    setUserData(data);
    setIsLoggedIn(true);
  };

  const handleSurveyComplete = (answers) => {
    setIsSurveyComplete(true);
  };

  return (
    <div className="app-wrapper">
      {!isLoggedIn ? (
        <Login onComplete={handleLoginComplete} />
      ) : !isSurveyComplete ? (
        <Survey
          participantNumber={userData.participantNumber}
          age={userData.age}
          education={userData.education}
          onComplete={handleSurveyComplete}
        />
      ) : (
        <EndPage />
      )}
    </div>
  );
}

export default App;
